from .models import Users, Moviesfrom .data_manager_interface import DataManagerInterfacefrom flask_sqlalchemy import SQLAlchemydb = SQLAlchemy()class SQLiteDataManager(DataManagerInterface):    def __init__(self, database_uri, db):        self.database_uri = database_uri        self.db = db  # Store the db object    def add_user(self, name):        new_user = Users(name=name)        self.db.session.add(new_user)        self.db.session.commit()        return new_user    def get_all_users(self):        return Users.query.all()    def get_user_by_id(self, user_id):        return Users.query.get(user_id)    def add_movie(self, title, director, year, genre, rating, user):        new_movie = Movies(title=title, director=director, year=year, genre=genre, rating=rating, user=user)        self.db.session.add(new_movie)        self.db.session.commit()    def delete_movie(self, movie_id):        movie = Movies.query.get(movie_id)        if movie:            self.db.session.delete(movie)            self.db.session.commit()    def update_movie(self, movie_id, title, director, year, genre, rating):        movie = Movies.query.get(movie_id)        if movie:            movie.title = title            movie.director = director            movie.year = year            movie.genre = genre            movie.rating = rating            self.db.session.commit()        return movie    def get_movie_by_id(self, movie_id):        return Movies.query.get(movie_id)